import {
  Layout,
  Form,
  Row,
  Col,
  Select,
  Input,
  Button,
  Space,
  Table,
  Image,
  Tag,
  Tooltip,
  Modal,
  message,
} from "antd";
import { SearchOutlined, ExclamationCircleFilled } from "@ant-design/icons";
import { useState, useEffect, useRef } from "react";
import { useStore } from "../../store";

import styles from "./index.module.css";

const { Content } = Layout;
const { Option } = Select;

const COLUMNS = [
  {
    title: "Name",
    dataIndex: "name",
    key: "name",
    ellipsis: true,
    width: 150,
  },
  {
    title: "Picture",
    dataIndex: "picture",
    key: "picture",
    ellipsis: true,
    width: 120,
    render: (data) => (
      <Image
        alt=""
        width={50}
        height={50}
        src={
          data
            ? data
            : "data:image/png;base64,"
        }
      />
    ),
  },
  {
    title: "Category",
    dataIndex: "category",
    key: "category",
    ellipsis: true,
    width: 120,
    render: (text) => (text ? <Tag color="geekblue">{text}</Tag> : "-"),
  },
  {
    title: "Stock",
    dataIndex: "stock",
    width: 80,
    key: "stock",
  },
  {
    title: "Description",
    dataIndex: "description",
    key: "description",
    ellipsis: true,
    render: (text) => (
      <Tooltip placement="topLeft" title={text}>
        {text}
      </Tooltip>
    ),
  },
  {
    title: "State",
    dataIndex: "state",
    width: 120,
    key: "state",
    render: (text) => (
      <Tag color={text === "available" ? "green" : "volcano"}>{text}</Tag>
    ),
  },
];

const SearchForm = ({ onFinish, onClear }) => {
  return (
    <Form name="search" size="small" onFinish={onFinish}>
      <Row gutter={24}>
        <Col span={8}>
          <Form.Item name="name" label="Equipment Name">
            <Input placeholder="e.g. ThinkBook 15 Gen 4" />
          </Form.Item>
        </Col>
        <Col span={5}>
          <Form.Item name="category" label="Category">
            <Input placeholder="e.g. Laptop" />
          </Form.Item>
        </Col>
        <Col span={5}>
          <Form.Item name="state" label="State">
            <Select placeholder="Please select" allowClear>
              <Option key="available" value="available">
                Available
              </Option>
              <Option key="unavailable" value="unavailable">
                Unavailable
              </Option>
            </Select>
          </Form.Item>
        </Col>
        <Col span={6}>
          <Form.Item>
            <Space wrap>
              <Button
                type="primary"
                htmlType="submit"
                icon={<SearchOutlined />}
              >
                Search
              </Button>
              <Button htmlType="reset" onClick={onClear}>
                Clear
              </Button>
            </Space>
          </Form.Item>
        </Col>
      </Row>
    </Form>
  );
};

function Equipment() {
  const refContent = useRef();
  const { equipStore } = useStore();

  const [params, setParams] = useState({});
  const [pagination, setPagination] = useState({});
  const [scrollHeight, setScrollHeight] = useState(184);
  const [data, setData] = useState({});

  useEffect(() => {
    // resize the table-scroll based on the height of the Content component
    const resizeScrollY = () => {
      setScrollHeight(refContent.current.clientHeight - 250);
    };

    // load the table for the first time and then resize
    /* ************ Warning !!!! ***********
     * This piece of code should not exist, its purpose is first resize.
     * I could't find a better trigger event, so I have to load data here once.
     * I tried the window.load event, but didn't work well.
     * ************************************* */
    equipStore.getEquips().then(setData).then(resizeScrollY);

    window.addEventListener("resize", resizeScrollY);
    return () => window.removeEventListener("resize", resizeScrollY);
  }, []);

  // update the data of the current page
  const updateTable = () => {
    const { current, pageSize } = pagination;
    equipStore.getEquips({ ...params, current, pageSize }).then(setData);
  };

  // update data when params or pagination is changed.
  useEffect(updateTable, [params, pagination]);

  const showDeleteConfirm = (id) => {
    console.log(id);
    Modal.confirm({
      title: "Are you sure delete it?",
      icon: <ExclamationCircleFilled />,
      okText: "Yes",
      okType: "danger",
      cancelText: "No",
      async onOk() {
        await equipStore.delEquip(id);
        message.success("Delete successfully");
        updateTable();
      },
    });
  };

  const columns = [
    ...COLUMNS,
    {
      title: "Action",
      dataIndex: "",
      key: "action",
      render: (_, row) => (
        <Space size="small" wrap>
          <Button type="link" onClick={() => {}}>
            Edit
          </Button>
          <Button type="link" danger onClick={() => showDeleteConfirm(row.id)}>
            Delete
          </Button>
        </Space>
      ),
    },
  ];

  return (
    <Content ref={refContent} className={styles.content}>
      <Table
        rowKey="id"
        className={styles.table}
        dataSource={data.list}
        columns={columns}
        pagination={{ ...pagination, total: data.total, showSizeChanger: true }}
        tableLayout="fixed"
        scroll={{ y: scrollHeight }}
        title={() => (
          <SearchForm
            onFinish={(values) => {
              setParams(values);
              setPagination({ current: 1 });
            }}
            onClear={() => {
              setParams({});
              setPagination({ current: 1 });
            }}
          />
        )}
        onChange={setPagination}
      />
    </Content>
  );
}

export default Equipment;
