import {
  Layout,
  Form,
  Row,
  Col,
  Select,
  Input,
  Button,
  Space,
  Table,
  Image,
  Tag,
  Tooltip,
} from "antd";
import { SearchOutlined } from "@ant-design/icons";
import { useState, useEffect, useRef, useMemo } from "react";
import { useStore } from "../../store";

import styles from "./index.module.css";

const { Content } = Layout;
const { Option } = Select;

const columns = [
  {
    title: "Name",
    dataIndex: "name",
    key: "name",
    ellipsis: true,
    width: 150,
  },
  {
    title: "Picture",
    dataIndex: "picture",
    key: "picture",
    ellipsis: true,
    width: 120,
    render: () => (
      <Image
        alt=""
        width={50}
        height={50}
        src={
          "data:image/png;base64,"
        }
      />
    ),
  },
  {
    title: "Category",
    dataIndex: "category",
    key: "category",
    ellipsis: true,
    width: 120,
    render: () => <Tag color="geekblue">Category</Tag>,
  },
  {
    title: "Description",
    dataIndex: "description",
    key: "description",
    ellipsis: true,
    render: () => (
      <Tooltip placement="topLeft" title="Description">
        Description
      </Tooltip>
    ),
  },
  {
    title: "Stock",
    dataIndex: "stock",
    width: 80,
    key: "stock",
  },
  {
    title: "State",
    dataIndex: "state",
    width: 120,
    key: "state",
    render: (text) => (
      <Tag color={text === "available" ? "green" : "volcano"}>{text}</Tag>
    ),
  },
  {
    title: "Action",
    dataIndex: "",
    key: "action",
    render: () => (
      <Space size="small" wrap>
        <Button type="link" onClick={() => {}}>
          Edit
        </Button>
        <Button type="link" danger onClick={() => {}}>
          Delete
        </Button>
      </Space>
    ),
  },
];

const SearchForm = ({ onFinish, onClear }) => {
  return (
    <Form name="search" size="small" onFinish={onFinish}>
      <Row gutter={24}>
        <Col span={8}>
          <Form.Item name="name" label="Equipment Name">
            <Input placeholder="e.g. ThinkBook 15 Gen 4" />
          </Form.Item>
        </Col>
        <Col span={5}>
          <Form.Item name="category" label="Category">
            <Input placeholder="e.g. Laptop" />
          </Form.Item>
        </Col>
        <Col span={5}>
          <Form.Item name="state" label="State">
            <Select placeholder="Please select" allowClear>
              <Option key="available" value="available">
                Available
              </Option>
              <Option key="unavailable" value="unavailable">
                Unavailable
              </Option>
            </Select>
          </Form.Item>
        </Col>
        <Col span={6}>
          <Form.Item>
            <Space wrap>
              <Button
                type="primary"
                htmlType="submit"
                icon={<SearchOutlined />}
              >
                Search
              </Button>
              <Button htmlType="reset" onClick={onClear}>
                Clear
              </Button>
            </Space>
          </Form.Item>
        </Col>
      </Row>
    </Form>
  );
};

function Equipment() {
  const refContent = useRef();
  const { equipStore } = useStore();

  const [params, setParams] = useState({});
  const [pagination, setPagination] = useState({});
  const [scrollHeight, setScrollHeight] = useState(184);
  const [data, setData] = useState({});

  useEffect(() => {
    // resize the table-scroll based on the height of the Content component
    const resizeScrollY = () => {
      setScrollHeight(refContent.current.clientHeight - 250);
    };

    // load the table for the first time and then resize
    equipStore.getEquips().then(setData).then(resizeScrollY);

    window.addEventListener("resize", resizeScrollY);
    return () => window.removeEventListener("resize", resizeScrollY);
  }, []);

  // update data when params or pagination is changed.
  useEffect(() => {
    const { current, pageSize } = pagination;
    equipStore.getEquips({ ...params, current, pageSize }).then(setData);
  }, [params, pagination]);

  return (
    <Content ref={refContent} className={styles.content}>
      <Table
        className={styles.table}
        dataSource={data.list}
        columns={columns}
        pagination={{ ...pagination, total: data.total, showSizeChanger: true }}
        tableLayout="fixed"
        scroll={{ y: scrollHeight }}
        title={() => (
          <SearchForm
            onFinish={(values) => {
              setParams(values);
              setPagination({current:1});
            }}
            onClear={() => {
              setParams({});
              setPagination({current:1});
            }}
          />
        )}
        onChange={setPagination}
      />
    </Content>
  );
}

export default Equipment;
